import React, { useState } from "react";

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendChat = async () => {
    if (!input.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { from: "user", content: input }]);
    setInput("");

    try {
      // Call OpenAI API
      const res = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo",
          messages: [{ role: "user", content: input }],
          max_tokens: 150,
        }),
      });

      if (!res.ok) {
        throw new Error(`OpenAI error: ${res.status}`);
      }

      const data = await res.json();

      // Add bot response message
      setMessages((prev) => [
        ...prev,
        { from: "bot", content: data.choices[0].message.content.trim() },
      ]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { from: "bot", content: "‚ùå Error connecting to AI." },
      ]);
      console.error(err);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center" }}>üß† InsureMitra AI Chatbot</h1>

      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: 8,
          padding: 16,
          height: 300,
          overflowY: "auto",
          marginBottom: 10,
          backgroundColor: "#f9f9f9",
        }}
      >
        {messages.length === 0 && <p style={{ color: "#666" }}>Ask your insurance questions here...</p>}
        {messages.map((msg, i) => (
          <div
            key={i}
            style={{
              textAlign: msg.from === "user" ? "right" : "left",
              margin: "8px 0",
            }}
          >
            <b style={{ color: msg.from === "user" ? "#1e90ff" : "#228B22" }}>
              {msg.from === "user" ? "You" : "Mitra"}
            </b>
            <p
              style={{
                display: "inline-block",
                padding: "8px 12px",
                borderRadius: 16,
                backgroundColor: msg.from === "user" ? "#cce4ff" : "#d4edda",
                maxWidth: "75%",
                wordWrap: "break-word",
                margin: 0,
              }}
            >
              {msg.content}
            </p>
          </div>
        ))}
      </div>

      <div style={{ display: "flex", gap: 8 }}>
        <input
          style={{ flexGrow: 1, padding: 8, borderRadius: 4, border: "1px solid #ccc" }}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask in Hindi or English"
          onKeyDown={(e) => e.key === "Enter" && sendChat()}
        />
        <button
          style={{
            padding: "8px 16px",
            backgroundColor: "#1e90ff",
            color: "white",
            border: "none",
            borderRadius: 4,
            cursor: "pointer",
          }}
          onClick={sendChat}
        >
          Send
        </button>
      </div>
    </div>
  );
}
