// src/Chatbot.js
import React, { useState } from "react";
import "./Chatbot.css"; // Optional for styling

const faq = {
  "what insurance plans do you offer": "We offer health, vehicle, and life insurance plans.",
  "how do i file a claim": "You can file a claim via our website or mobile app under the 'Claims' section.",
  "what documents are required for claim": "You need to submit your policy number, ID proof, and claim form.",
  "how can i contact support": "You can contact our support team at support@insuremitra.com or call 1800-123-456.",
};

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const getBotReply = (message) => {
    const query = message.toLowerCase();
    for (let key in faq) {
      if (query.includes(key)) {
        return faq[key];
      }
    }
    return "Sorry, I didn't understand that. Please ask about insurance plans, claims, or support.";
  };

  const handleSend = () => {
    if (!input.trim()) return;

    const userMsg = { type: "user", text: input };
    const botMsg = { type: "bot", text: getBotReply(input) };

    setMessages([...messages, userMsg, botMsg]);
    setInput("");
  };

  return (
    <div className="chatbot">
      <div className="chatbox">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.type}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me about insurance..."
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default Chatbot;
